// src/components/UserView.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { users } from '../data';
import axios from 'axios';
import AddUser from './AddUser';
// Dummy user data


const UserView = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  useEffect(() => {
    // Fetch users from the backend
    const fetchUsers = async () => {
      try {
        console.log(id)
        const response = await axios.get(`http://localhost:5000/api/users/${id}`);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);


  const handleSave = () => {
    // Save logic here
    navigate('/');
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div>

      <form onSubmit={handleSubmit}>
      <div>
          <label>ID:</label>
          <input type="text" name="id" value = {formData.id} onChange= {handleChange} required />
        </div>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value = {formData.name} onChange = {handleChange} required />
        </div>
        {/* <div>
          <label>P5 Balance:</label>
          <input type="number" name="p5Balance" required />
        </div>
        <div>
          <label>Reward Balance:</label>
          <input type="number" name="rewardBalance" required />
        </div> */}
        <button type="submit" id="save">Save</button>

      </form>
      <button type="submit" id="cancel" onClick = {handleSubmit}>Cancel</button>
    </div>
  );
};

export default UserView;
