// src/components/UserView.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

// Dummy user data
const users = [
  { id: 1, name: 'John Doe', p5Balance: 1200, rewardBalance: 300 },
  { id: 2, name: 'Jane Smith', p5Balance: 1500, rewardBalance: 500 },
];

const UserView = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchedUser = users.find(user => user.id === parseInt(id));
    setUser(fetchedUser);
  }, [id]);

  const handleSave = () => {
    // Save logic here
    navigate('/');
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div>
      <h1>View User</h1>
      <div>
        <label>Name: {user.name}</label>
      </div>
      <button onClick={() => navigate(`/${id}/p5`)}>View P5 Balance: {user.p5Balance}</button>
      <button onClick={() => navigate(`/${id}/rewards`)}>View Reward Balance: {user.rewardBalance}</button>
      <button onClick={handleSave}>Save</button>
      <button onClick={() => navigate('/')}>Cancel</button>
    </div>
  );
};

export default UserView;
